import React, { useState } from "react";
import firebase from "../firebase";


const AddUserForm = props => {
  const initialFormState = { id: null, name: "", email: "",phonenumber:"",salary:""};
  const [user, setUser] = useState(initialFormState);

  const handleInputChange = event => {
    const { name, value } = event.target;

    setUser({ ...user, [name]: value });
  };
  const [show, setShow] = useState(false);
const handleClose = () => setShow(false);
const handleShow = () => setShow(true);

  return (
    <form
      onSubmit={event => {
        event.preventDefault();
        if (!user.name || !user.email || !user.phonenumber || !user.salary)
          return;

        props.addUser(user);
        setUser(initialFormState);
      } }
    >
        <div className="col-xs-3">
          <label for="ex1">Name</label>
          <input className="form-control" id="ex1" type="text"
            name="name"
            value={user.name}
            onChange={handleInputChange} />
        </div>
        <div className="col-xs-3">
          <label>Email</label>
          <input class="form-control"
            type="text"
            name="email"
            value={user.email}
            onChange={handleInputChange} />
        </div>
        <div className="col-xs-3">
          <label>Phone Number</label>
          <input class="form-control"
            type="text"
            name="phonenumber"
            value={user.phonenumber}
            onChange={handleInputChange} />
        </div>
        <div className="col-xs-3">
          <label>Salary</label>
          <input class="form-control"
            type="text"
            name="salary"
            value={user.salary}
            onChange={handleInputChange} />
        </div><br/>
        &nbsp;&nbsp;<button class="btn btn-primary" onClick={handleShow} style={{ margin: "5px" }}>Add new Empolyee</button>
      </form>
  );
};

export default AddUserForm;
